import React, { useState } from "react";
import styled from "styled-components";

import styles from "./HoverFx.module.scss";

import BGImg from "../images/debut_light.png";
import TrainingBotPic from "../images/TrainingBotHome.png";

function Projects(props) {
  const [trainingBot, setTrainingBot] = useState(true);

  return (
    <PortfolioWrapper>
      <h2>Projects</h2>
      <Project style={{ backgroundImage: `url(${TrainingBotPic})` }}>
        <div
          className={trainingBot ? styles.popoverHover : styles.popoverHoverOut}
        />
      </Project>
    </PortfolioWrapper>
  );
}

export default Projects;

const PortfolioWrapper = styled.div`
  width: 100vw;
  padding: 0 55px;
  min-height: 100vh;
  background-image: url(${BGImg});
  h2 {
    padding: 80px 0;
    font-size: 45px;
    color: black;
    margin: 0;
  }
`;

const Project = styled.div`
  margin: auto;
  width: 80vw;
  height: 175px;
  background: #28af3e;
  background-size: cover;
  background-position: center;
  box-shadow: 0 5px 5px rgba(0, 0, 0, 0.3);
  position: relative;
  div {
    position: absolute;
    width: 100%;
    height: 100%;
    background: rgba(96%, 23%, 33%, 0.85);
  }
`;
