import React from "react";
//import Typist from "react-typist";
import BackgroundImg from "../images/baron14.jpg";
import styled from "styled-components";
import Parallax from "react-rellax";
import Particles from "react-particles-js";

const particlesOptions = {
  particles: {
    number: {
      value: 52,
      density: {
        enable: true,
        value_area: 2000
      }
    },
    color: {
      value: "#ffffff"
    },
    shape: {
      type: "circle",
      stroke: {
        width: 1,
        color: "#bbbbbb"
      },
      polygon: {
        nb_sides: 5
      },
      image: {
        src: "img/github.svg",
        width: 100,
        height: 100
      }
    },
    opacity: {
      value: 0.5,
      random: false,
      anim: {
        enable: false,
        speed: 1,
        opacity_min: 0.1,
        sync: false
      }
    },
    size: {
      value: 2.891476416322726,
      random: true,
      anim: {
        enable: false,
        speed: 40,
        size_min: 0.1,
        sync: false
      }
    },
    line_linked: {
      enable: true,
      distance: 150,
      color: "#ffffff",
      opacity: 0.1,
      width: 1
    },
    move: {
      enable: true,
      speed: 1.206824121731046,
      direction: "none",
      random: false,
      straight: false,
      out_mode: "out",
      bounce: false,
      attract: {
        enable: false,
        rotateX: 600,
        rotateY: 1200
      }
    }
  },
  interactivity: {
    detect_on: "canvas",
    events: {
      onhover: {
        enable: true,
        mode: "bubble"
      },
      onclick: {
        enable: true,
        mode: "push"
      },
      resize: true
    },
    modes: {
      grab: {
        distance: 400,
        line_linked: {
          opacity: 1
        }
      },
      bubble: {
        distance: 400,
        size: 0,
        duration: 2,
        opacity: 8,
        speed: 3
      },
      repulse: {
        distance: 200,
        duration: 0.4
      },
      push: {
        particles_nb: 4
      },
      remove: {
        particles_nb: 2
      }
    }
  },
  retina_detect: true
};

export default function HeroSection() {
  return (
    <Parallax speed={4}>
      <HeroWrapper>
        <Hero>
          <h1>Thomas Hessburg</h1>
          <h2>Full Stack Web Developer</h2>
        </Hero>
        <HeroParticles className="particles" params={particlesOptions} />
      </HeroWrapper>
    </Parallax>
  );
}

const HeroWrapper = styled.div`
  height: 100vh;
  position: relative;
  overflow: hidden;
  background-image: url(${BackgroundImg});
  background-size: cover;
  background-position: center;
  background-color: black;
  box-shadow: 10px 10px 15px rgba(0, 0, 0, 0.4);
  opactiy: 0.4;
`;

const Hero = styled.div`
  display: block;
  width: 100vw;
  height: 100vh;
  background: rgba(18%, 22%, 31%, 0.8);
  color: white;
  h1 {
    font-family: "Halant", serif;
    font-weight: 200;
    padding: 120px 0 0 0;
    margin: 0;
    font-size: 100px;
  }
  h2 {
    font-family: "Cormorant Unicase", serif;
    font-size: 40px;
    margin: 0;
  }
`;

const HeroParticles = styled(Particles)`
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1;
  height; 100vh;

`;
