import React from "react";
import styled from "styled-components";
import BGTexture from "../images/ep_naturalblack.png";

export default function AboutMe() {
  return (
    <AboutMeStripe style={{ backgroundImage: `url(${BGTexture})` }}>
      <AboutMeInfo>
        <h2>About Me</h2>
        <hr />
        <div>
          <p>
            Hi, everyone! My name is Thomas Hessburg, and I'm a full stack
            software developer from the Orlando, Florida area. I love to create,
            and I'm excited to lern and grow in the tech field. After spending
            the past several years working as a welder/fabricator in the oil
            fields, and well as working for myself as an audio engineer writing
            and mixing music for bands/music groups, I decided to take on a new
            challenge in learning to create software. I began my journey in
            mid-2018 by picking up some bookes/courses on JavaScript and
            spending 12 hour days trying to learn EVERYTHING that I possibly
            could. Come early 2019, I began attending Lambda School, an online,
            fully immersive, 30+ week dev bootcamp. I can proudly say that after
            over half a year and over 1'000 hours, I'm ready to make my move
            into the workforce. I would love to bring my talents to your
            company!
          </p>
        </div>
      </AboutMeInfo>
    </AboutMeStripe>
  );
}

const AboutMeStripe = styled.div`
  margin: 100px auto;
  position: aboslute;
  display: flex;
  justify-content: center;
  padding: 300px 40px;
  z-index: -70;
  top: 100vh;
  width: 100vw;
  background: black;
  clip-path: polygon(0 0, 100% 22%, 100% 100%, 0 78%);
`;

const AboutMeInfo = styled.div`
  marign: auto;
  width: 100%;
  min-height: 300px;
  max-width: 1200px;
  padding: 15px;
  background: white;
  font-family: "Julius Sans One", sans-serif;
  h2 {
    font-size: 40px;
    margin: 10px;
    font-weight: 100;
  }
`;
