import React, { useState } from "react";
import Typist from "react-typist";
import BackgroundImg from "../images/baron14.jpg";
import styled from "styled-components";
import Parallax from "react-rellax";
import BGTexture from "../images/pipes.png";

export default function HeroSection() {
  const [typeStatus, setTypeStatus] = useState(true);
  return (
    <div>
      <div
        style={{
          width: "100vw",
          overflow: "visible",
          position: "relative",
          zIndex: "5"
        }}
      >
        <LandingCard>
          <NameContainer>
            <h1>Thomas Hessburg</h1>
            <br />
            <p>Software Developer</p>
          </NameContainer>
          <TyperContainer>
            {typeStatus && (
              <Typist
                cursor={{
                  show: false
                }}
                onTypingDone={() => {
                  setTypeStatus(false);
                  setTypeStatus(true);
                }}
              >
                <Parallax speed={2}>
                  <h2>React</h2>
                  <Typist.Backspace count={5} delay={800} />
                  <h2>Redux</h2>
                  <Typist.Backspace count={5} delay={800} />
                  <h2>Node</h2>
                  <Typist.Backspace count={4} delay={800} />
                  <h2>Express</h2>
                  <Typist.Backspace count={7} delay={800} />
                </Parallax>
              </Typist>
            )}
          </TyperContainer>
        </LandingCard>
      </div>
      <BGImg src={BackgroundImg} />
    </div>
  );
}

const LandingCard = styled.div`
  margin: 35vh auto;
  font-family: "Julius Sans One", sans-serif;
  margin-left: 50vw;
  max-width: 800px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  width: 80%;
  height: 30vh;
  max-height: 280px;
  background: rgba(0, 0, 0, 0.9);
  box-shadow: 0 5px 18px rgba(10, 10, 10, 0.35);
  position: relative;
  @media (max-width: 1700px) {
    margin-left: 45vw;
  }
  @media (max-width: 1600px) {
    margin-left: 38vw;
  }
  @media (max-width: 1500px) {
    margin-left: 35vw;
  }
  @media (max-width: 1400px) {
    margin-left: 33vw;
  }
  @media (max-width: 1600px) {
    margin-left: 39vw;
    height: 25vh;
  }
  @media (max-width: 1500px) {
    margin-left: 27vw;
  }
  @media (max-width: 1400px) {
    margin-left: 26vw;
  }
  @media (max-width: 1300px) {
    margin-left: 24vw;
  }
  @media (max-width: 1200px) {
    margin-left: 20vw;
  }
  @media (max-width: 1100px) {
    margin-left: 12vw;
  }
  h2 {
    font-size: 70px;
    color: rgb(255, 255, 255);
    font-weight: 100;
    @media (max-width: 600px) {
      font-size: 55px;
    }
  }
`;

const TyperContainer = styled.div`
  display: flex;
`;

const NameContainer = styled.div`
  background: rgba(43, 53, 71, 1);
  color: white;
  position: absolute;
  transform: rotate(2.5deg);
  top: -95px;
  right: -40px;
  padding: 5px 30px;
  box-shadow: 0 3px 3px 2px rgba(0, 0, 0, 0.2);
  h1 {
    font-size: 30px;
    margin-bottom: 0px;
    @media (max-width: 600px) {
      font-size: 25px;
    }
  }
  p {
    margin-top: 0;
    font-size: 18px;
    @media (max-width: 600px) {
      font-size: 14px;
    }
  }
`;

const BGImg = styled.img`
  position: absolute;
  top: 0px;
  left: 0;
  z-index: 3;
  width: 40vw;
  min-width: 370px;
  border-bottom-right-radius: 150px;
  box-shadow: 4px 10px 4px 2px rgba(0, 0, 0, 0.3);
`;
