import React from "react";
//import Typist from "react-typist";
import BackgroundImg from "../images/baron14.jpg";
import styled from "styled-components";
//import Parallax from "react-rellax";

export default function HeroSection() {
  return (
    <HeroWrapper>
      <BGImg src={BackgroundImg} />
      <DeskTopHeader>
        <h1>Thomas Hessburg</h1>
      </DeskTopHeader>
      <MobileHero />
    </HeroWrapper>
  );
}

const HeroWrapper = styled.div`
  height: 100vh;
  @media (max-width: 900px) {
    background-image: url(${BackgroundImg});
    background-size: cover;
    background-position: center;
    background-color: black;
    border-bottom: 10px solid black;
    opactiy: 0.4;
  }
`;

const BGImg = styled.img`
  position: absolute;
  top: 0px;
  left: 0;
  z-index: 3;
  width: 40vw;
  min-width: 440px;
  max-width: 580px;
  border-bottom-right-radius: 150px;
  box-shadow: 4px 10px 4px 2px rgba(0, 0, 0, 0.3);
  @media (max-width: 900px) {
    display: none;
  }
`;

const DeskTopHeader = styled.div`
  position: absolute;
  top: 0px;
  right: 0px;
  z-index: 2;
  width: 65%;
  height: 100vh;
  min-width: 440px;
  clip-path: polygon(22% 0, 100% 1%, 100% 100%, 50% 100%, 0 99%, 0 0);
  display: flex;
  justify-content: center;
  box-shadow: inset 0 0 5px black;

  h1 {
    margin-top: 100px;
    font-size: 60px;
  }

  @media (max-width: 900px) {
    display: none;
  }
`;

const MobileHero = styled.div`
  display: none;

  @media (max-width: 900px) {
    display: block;
    width: 100vw;
    height: 100vh;
    background: rgba(18%, 22%, 31%, 0.75);
  }
`;
