import React from "react";
//import Typist from "react-typist";
import BackgroundImg from "../images/baron14.jpg";
import styled from "styled-components";
//import Parallax from "react-rellax";
import Particles from "react-particles-js";

const particlesOptions = {
  particles: {
    number: {
      value: 6,
      density: {
        enable: true,
        value_area: 800
      }
    },
    color: {
      value: "#e8e8e8"
    },
    shape: {
      type: "circle",
      stroke: {
        width: 0,
        color: "#2915c3"
      },
      polygon: {
        nb_sides: 3
      },
      image: {
        src: "img/github.svg",
        width: 100,
        height: 100
      }
    },
    opacity: {
      value: 0.3,
      random: true,
      anim: {
        enable: false,
        speed: 1,
        opacity_min: 0.1,
        sync: false
      }
    },
    size: {
      value: 31.565905665290902,
      random: true,
      anim: {
        enable: true,
        speed: 10,
        size_min: 40,
        sync: false
      }
    },
    line_linked: {
      enable: false,
      distance: 208.44356791251798,
      color: "#ffffff",
      opacity: 0.9700642968236413,
      width: 11.384225632145213
    },
    move: {
      enable: true,
      speed: 4.810236182596568,
      direction: "bottom",
      random: false,
      straight: false,
      out_mode: "out",
      bounce: false,
      attract: {
        enable: true,
        rotateX: 3367.1653278175977,
        rotateY: 1200
      }
    }
  },
  interactivity: {
    detect_on: "canvas",
    events: {
      onhover: {
        enable: false,
        mode: "bubble"
      },
      onclick: {
        enable: true,
        mode: "push"
      },
      resize: true
    },
    modes: {
      grab: {
        distance: 400,
        line_linked: {
          opacity: 1
        }
      },
      bubble: {
        distance: 400,
        size: 40,
        duration: 2,
        opacity: 8,
        speed: 3
      },
      repulse: {
        distance: 200,
        duration: 0.4
      },
      push: {
        particles_nb: 4
      },
      remove: {
        particles_nb: 2
      }
    }
  },
  retina_detect: true
};
export default function HeroSection() {
  return (
    <HeroWrapper>
      <Hero />
      <HeroParticles className="particles" params={particlesOptions} />
    </HeroWrapper>
  );
}

const HeroWrapper = styled.div`
  height: 100vh;
  position: relative;
  overflow: hidden;
  background-image: url(${BackgroundImg});
  background-size: cover;
  background-position: center;
  background-color: black;
  border-bottom: 10px solid black;
  opactiy: 0.4;
`;

const Hero = styled.div`
  display: block;
  width: 100vw;
  height: 100vh;
  background: rgba(18%, 22%, 31%, 0.75);
`;

const HeroParticles = styled(Particles)`
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1;
  height; 100vh;

`;
